import React, { Component } from 'react';
import './App.css';

import Permanent from './src/Permanent/Permanent';
import Contract from './src/Contract/Contract';
import SignIn from './src/Signin/Signin';
import List from './src/List/List';
import Header from './src/List/header';
class App extends Component {
  state = {
    contract: [
      { 
        id:"C01",
        name: "Manu Kapoor",
        email: "manu.kapoor@wipro.com",
        company: "Wipro",
        mobile: "9160808366" 
      },
      { 
        id:"C02",
        name: "Manu Kapoor 2",
        email: "manu.kapoor@wipro.com",
        company: "Wipro",
        mobile: "9160808366" 
      }
    ],
    permanent: [
      { 
        id:"P01",
        name: "Suresh",
        email: "manu.kapoor@wipro.com",
        company: "Wipro",
        mobile: "9160808366" 
      },
      { 
        id:"P02",
        name: "Suresh 2",
        email: "manu.kapoor@wipro.com",
        company: "Wipro",
        mobile: "9160808366" 
      }
    ],
    showContract: false,
  }

  toggleEmployeesHandler = () => {
    const doesShow = this.state.showContract;
    this.setState({ showContract: !doesShow })
  }
  deleteEmployee = (employeeIndex) =>{
    const contract = [...this.state.contract];
    contract.splice(employeeIndex,1);
    this.setState({contract: contract});
  }
  addEmployee = () =>{
    let newEmploye = [];
    const contract = [...this.state.contract].concat([...this.state.contract]);
    newEmploye.push(contract);
    alert(JSON.stringify(newEmploye));
  }
  render() {
    let contractEmployees= null;
    let permanentEmployees= null;

    if(this.state.showContract){
      contractEmployees =(
        (this.state.contract.map((item, index) => {
          return <Contract 
          key = {item.id}
          name = {item.name} 
          email = {item.email} 
          company = {item.company} 
          mobile = {item.mobile}
          onDelete={()=>this.deleteEmployee(index)} 
          />
        }))
      )
    }
    else{
      permanentEmployees =(
        (this.state.permanent.map(item =>{
          return <List 
          key = {item.id} // to avoid re-render of complete data, just re-render the changes in the DOM. and renders updated changes
          name = {item.name} 
          email = {item.email} 
          company = {item.company} 
          mobile = {item.mobile} 
          />
        }))
        )
    }
    return (
      <div className="App">
        {/* <button onClick={this.toggleEmployeesHandler}>Toggle Employees</button> */}
          {/* {contractEmployees}
          {permanentEmployees} */}
          {/* <button onClick={this.addEmployee}>Toggle Employees</button>  */}
          {/* <SignIn/>  */}
          <List/>
          <Header/>
        {permanentEmployees} 
      </div>
    );
  }
}

export default App;
