import React, { Component } from 'react';
import './App.css';

import Permanent from './src/Permanent/Permanent';
import Contract from './src/Contract/Contract';


class App extends Component {
  state = {
    contract: [
      { name: "Manu Kapoor" },
      { email: "manu.kapoor@wipro.com" },
      { company: "Wipro" },
      { mobile: "9160808366" },
    ],

    permanent: [
      { name: "Ramesh kakarta" },
      { email: "ramesh@wipro.com" },
      { company: "Telstra" },
      { mobile: "9876543210" },
    ],
    showPermanent: false,
    showContract: false,
  }


  togglePersonsHandler = () => {
    const doesShow = this.state.showPersons;
    this.setState({ showPersons: !doesShow })
  }

  toggleEmployeesHandler = () => {
    const doesShow = this.state.showContract;
    this.setState({ showContract: !doesShow })
  }
  render() {
    let contract= null;
    let permanent= null;

    if(this.state.showContract){
      contract =(
        (this.state.contract.map(employee =>{
          return <Contract name = {employee.name} />
        }))
      )
    }
    else{
      permanent =(
        (this.state.permanent.map(employee1 =>{
          return <Permanent name = {employee1.name} />
        }))
        )

    }
    return (
      <div className="App">
        <button onClick={this.togglePersonsHandler}>Toggle</button>
        <button onClick={this.toggleEmployeesHandler}>Toggle Employees</button>
          {contract}
          {permanent}
      </div>

    );
  }
}

export default App;
