import React, { Component } from 'react';
import './App.css';

import Person from './Persons/Person';
import Permanent from './src/Permanent/Permanent';
import Contract from './src/Contract/Contract';
import UserRegistration from './UserRegistration/UserRegistration';

class App extends Component {
  state = { 
    contract :[
      {name: "Manu Kapoor"},
      {email: "manu.kapoor@wipro.com"},
      {company: "Wipro"},
      {mobile: "9160808366"},
    ],

    permanent :[
      {name: "Ramesh kakarta"},
      {email: "ramesh@wipro.com"},
      {company: "Telstra"},
      {mobile: "9876543210"},
    ],
    showPermanent: false,
    showContract: false,
  }


  togglePersonsHandler = () => {
    const doesShow = this.state.showPersons;
    this.setState({ showPersons: !doesShow })
  }

  toggleEmployeesHandler = () =>{
    const doesShow = this.state.showContract;
    this.setState({ showContract: !doesShow })
  }
  render() {
    return (
      <div className="App">
        {/* <h1>Welcome</h1> */}
        {/* <Person name ={this.state.persons[0].name}
       onChanged = {this.nameChangeHandler}/> */}
        <button onClick={this.togglePersonsHandler}>Toggle</button>
        <button onClick={this.toggleEmployeesHandler}>Toggle Employees</button>
        {/* {persons} */}
        {this.state.permanent===true ? 
          <div>
        <Contract
         name = {this.state.contract[0].name}/>
          </div>  :null
      }

        <Permanent 
        name= {this.state.permanent[0].name}/>
      </div>

    );
  }
}

export default App;
