import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

import Person from './Persons/Person';

class App extends Component {
  state = {
    persons : [
      {name: "Manu"}
    ]
  }

  onChangeHandlerPerson = () => {
    // this.setState = ({
    //   persons : [
    //     {name: "Williams"}
    //   ]
    // })
    this.state.persons[0].name = "Pune"
  }
  render() {
    return (
      <div className="App">
       <h1>Welcome</h1>
       <Person name ={this.state.persons[0].name} 
       changeName = {this.onChangeHandlerPerson}/>
       <button onClick = {this.onChangeHandlerPerson}>Switch Name</button>
      </div>
    );
  }
}

export default App;
