import React, { Component } from 'react';
import './App.css';

import Person from './BackLogs/Persons/Person';
import Dashboard from './Dashboard/Dashboard';
import Permanent from './BackLogs/src/Permanent/Permanent';
import Contract from './BackLogs/src/Contract/Contract';
import UserRegistration from './BackLogs/UserRegistration/UserRegistration';

class App extends Component {
  state = {
    persons: [
      { email: "manu.k@kko.com" },
      { password: "manu.k@kko.com" }
    ],
    contract :[
      {name: "Manu Kapoor"},
      {email: "manu.kapoor@wipro.com"},
      {company: "Wipro"},
      {mobile: "9160808366"},
    ],

    permanent :[
      {name: "Ramesh kakarta"},
      {email: "ramesh@wipro.com"},
      {company: "Telstra"},
      {mobile: "9876543210"},
    ],
    showPersons: false,
    showPermanent: false,
    showContract: true,
  }



  onChangeHandlerPerson = () => {
    this.setState({
      persons: [
        { email: "" },
        { password: "" }
      ]
    })
    console.log("cl;ick");
  }

  nameChangeHandler = (email) => {
    this.setState({
      persons: [
        { email: email.target.value },
      ]
    })
  }
  nameChangeHandlerPassword = (password) => {
    this.setState({
      persons: [
        { password: password.target.value }
      ]
    })
  }
  togglePersonsHandler = () => {
    const doesShow = this.state.showPersons;
    this.setState({ showPersons: !doesShow })
  }

  toggleEmployeesHandler = () =>{
    const showEmployees = this.state.showContract;
    this.setState({showContract: !showEmployees})
  }
  render() {
    let persons = null;
    let employeesList = null;

    if (this.state.showPersons) {
      persons = (
        <div>
          {this.state.persons.map(person =>{
            return <UserRegistration
            name = {person.name}
            email= {person.email}/>
          })}
          {/* <UserRegistration
            name={this.state.persons[0].name}
            email={this.state.persons[0].email}
            password={this.state.persons[0].password}
            onChanged={this.nameChangeHandler}
            onChangedPass={this.nameChangeHandlerPassword} /> */}
          <button onClick={this.onChangeHandlerPerson}>Sign Up</button>
        </div>
      )

    }
    return (
      <div className="App">
        {/* <h1>Welcome</h1>
        {/* <Person name ={this.state.persons[0].name}
       onChanged = {this.nameChangeHandler}/> */}
        <button onClick={this.togglePersonsHandler}>Toggle</button>
        {/* <button onClick={this.toggleEmployeesHandler}>Toggle Employees</button> */}
        {/* {persons} */}
        {/* {this.state.permanent ? 
          <div>
        <Contract
         name = {this.state.contract[0].name}/>
          </div>  :null
      } */}

        {/* <Permanent 
        name= {this.state.permanent[0].name}/> */}
           <Dashboard/>
      </div>

    );
  }
}

export default App;
