import React, { Component } from 'react';
import './App.css';

import Permanent from './src/Permanent/Permanent';
import Contract from './src/Contract/Contract';


class App extends Component {
  state = {
    contract: [
      { 
        name: "Manu Kapoor",
        email: "manu.kapoor@wipro.com",
        company: "Wipro",
        mobile: "9160808366" 
      },

    ],
    permanent: [
      { 
        name: "Suresh",
        email: "manu.kapoor@wipro.com",
        company: "Wipro",
        mobile: "9160808366" 
      },
    ],
    showContract: false,
  }

  toggleEmployeesHandler = () => {
    const doesShow = this.state.showContract;
    this.setState({ showContract: !doesShow })
  }
  render() {
    let contractEmployees= null;
    let permanentEmployees= null;

    if(this.state.showContract){
      contractEmployees =(
        (this.state.contract.map(employee =>{
          return <Contract 
          name = {employee.name} 
          email = {employee.email} 
          company = {employee.company} 
          mobile = {employee.mobile} 
          />
        }))
      )
    }
    else{
      permanentEmployees =(
        (this.state.permanent.map(employee =>{
          return <Permanent 
          name = {employee.name} 
          email = {employee.email} 
          company = {employee.company} 
          mobile = {employee.mobile} 
          />
        }))
        )
    }
    return (
      <div className="App">
        <button onClick={this.toggleEmployeesHandler}>Toggle Employees</button>
          {contractEmployees}
          {permanentEmployees}
      </div>
    );
  }
}

export default App;
